# @lc code=start
class Solution:
    def longestValidParentheses(self, s: str) -> int:
        longest_from = [-1 for _ in s]
        for i in reversed(range(len(s))):
            stack = 0
            j = i
            while j < len(s):
                lfj = longest_from[j]
                if lfj <= 0:
                    if s[j] == '(':
                        stack += 1
                    else:
                        if stack == 1:
                            longest_from[i] =  j - i + 1
                        if stack == 0:
                            longest_from[i] =  j - i
                            break
                        stack -= 1
                    j += 1
                else:
                    j += lfj
            if stack > 0 and longest_from[i] == -1:
                longest_from[i] = 0
        if len(longest_from) == 0:
             return 0
        return max(longest_from)


